sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (newValue === 'admin') {
		g_form.clearOptions('effort');
		g_form.addOption('effort','admin_training','Admin – Training/Seminar (This is for Web based training, Classroom based training, Virtual training, Echo sessions, Seminars, etc)');
		g_form.addOption('effort','admin_meeting','Admin – Meeting (This is for 1:1, Staff, Team, All Hands, Town Hall, etc)');
		g_form.addOption('effort','admin_adhoc','Admin – AdHoc (This is for Request for Supplies, Gathering of signed forms, etc)');
	}else if(newValue === 'process_analyst'){
		g_form.clearOptions('effort');
		g_form.addOption('effort','','Application Banding');
		g_form.addOption('effort','','Incident Management');
		g_form.addOption('effort','','Change Management');
		g_form.addOption('effort','','Problem Management');
		g_form.addOption('effort','','Knowledge Management');
		g_form.addOption('effort','','Service Transition');
		g_form.addOption('effort','','Operational Handbook');
	}else if(newValue === 'product_analyst'){
		g_form.clearOptions('effort');
		g_form.addOption('effort','service_transition','Service Transition');
	}else if(newValue === 'analytics_and_dashboard_reporting'){
		g_form.clearOptions('effort');
		g_form.addOption('effort','','Resource Utilization Tool');
		g_form.addOption('effort','sdg_weekly_ops_deck','SDG Weekly Ops Deck');
		g_form.addOption('effort','is_weekly_ops_deck','IS Weekly Ops Deck');
		g_form.addOption('effort','sdg_dashboard','SDG Dashboard');
		g_form.addOption('effort','is_dashboard','IS Dashboard');
		g_form.addOption('effort','snow_dev_table_access','SNOW Development Table Access');
		g_form.addOption('effort','teradata_dev_access','Teradata Dev Access');
	}else if(newValue === 'communications_and_training'){
		g_form.clearOptions('effort');
		g_form.addOption('effort','snow_istanbul','SNOW Istanbul (UI16)');
		g_form.addOption('effort','snow_ui17','SNOW (UI17)');
		g_form.addOption('effort','scripting_course_outline_creations_update','Scripting and Course Outline Creations and Update');
	}else if(newValue === 'automation'){
		g_form.clearOptions('effort');
		g_form.addOption('effort','top5_automation_init','Top 5 Automation Initiatives');
		g_form.addOption('effort','non_top5_automation_init','Non Top 5 Automation Initiatives');
		g_form.addOption('effort','snow_dev_db_access','SNOW Development Database Access');
	}else if(newValue === 'automation'){
		g_form.clearOptions('effort');
		g_form.addOption('effort','top5_automation_init','Top 5 Automation Initiatives');
		g_form.addOption('effort','non_top5_automation_init','Non Top 5 Automation Initiatives');
		g_form.addOption('effort','snow_dev_db_access','SNOW Development Database Access');
	}else if(newValue === 'project'){
		g_form.clearOptions('effort');
		g_form.addOption('effort','atlas_portal','ATLAS Portal');
		g_form.addOption('effort','l1_l3_assessment','L1-L3 IS Assessment');
		g_form.addOption('effort','mfs_portal','MFS Portal');
		g_form.addOption('effort','is_portal','Infrastructure Services Portal');

	}
	else{
		g_form.clearOptions('effort');
		g_form.addOption('effort','none','--None--');
	}

}
